				type: 'category', axisLabel: { fontSize: 10, fontWeight: 900, interval:0, rotate:20 },
				data: ["测绘总工", "前期规划经理", "测绘组长", "铁路行业销售总监", "研发总监","Java架构师", "IT科技中心副总监", "地理信息系统专家", "主创景观设计师", "副总规划师"],
			},
