			radar: {
				radius: 140,
				name: {
					textStyle: {
						color: "#232323", backgroundColor: '#d0d0d0',
						borderRadius: 3, padding: [2, 1]
					}
				},
				indicator:
				[
					{name: '专业服务', max: 20}, {name: '政府/公共事业', max: 20},
					{name: '仪器仪表/工业自动化', max: 20}, {name: '环保/资源', max: 20},
