					data: [20.38, 18.19, 17.65, 18.62, 16.49, 16.3, 15.4, 14.75, 12.82, 12.56, 12.16, 11.74, 10.32, 9.89, 10.11]
				},
				{
					name: '平均月薪', type: 'line', yAxisIndex: 1,
					color: '#388fa2',
					data: [16.77, 14.685, 14.15, 13.56, 12.91, 12.79, 12.28, 11.74, 10.295, 10.005, 9.84, 9.225, 8.4, 8.1, 8.05]
