import scattermap from './components/scattermap.vue';
import heatmap from './components/heatmap.vue';
export default {
  data: function data() {
    return {
      isShow: false,
      scattermap: "scattermap",
      heatmap: "heatmap",
      currentView: 'scattermap'
    };
  },
  components: {
    scattermap: scattermap,
    heatmap: heatmap
  },
  methods: {
    toggleTabs: function toggleTabs(tabText) {
      this.currentView = tabText;
    },
    change: function change() {
      this.isShow = !this.isShow;
    }
  },
  // 功能区拖动
  directives: {
    drag: function drag(el) {
      var dragBox = el; //获取当前元素

      dragBox.onmousedown = function (e) {
        //算出鼠标相对元素的位置
        var disX = e.clientX - dragBox.offsetLeft;
        var disY = e.clientY - dragBox.offsetTop;

        document.onmousemove = function (e) {
          //用鼠标的位置减去鼠标相对元素的位置，得到元素的位置
          var left = e.clientX - disX;
          var top = e.clientY - disY; //移动当前元素

          dragBox.style.left = left + "px";
          dragBox.style.top = top + "px";
        };

        document.onmouseup = function (e) {
          //鼠标弹起来的时候不再移动
          document.onmousemove = null; //预防鼠标弹起来后还会循环（即预防鼠标放上去的时候还会移动）  

          document.onmouseup = null;
        };
      };
    }
  }
};
