.logo {
  margin-left: 50px;
  margin-top: 10px;
}
.loginName {
  margin-left: 7px;
  font-family: "微软雅黑";
  font-size: 40px;
  font-weight: bold;
  color: #19be6b;
  background-image: -webkit-gradient(
	linear,
	0 0,
	0 bottom,
	from(#ff6145),
	to(rgb(226, 46, 14))
  );
  background-clip: text;
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
}
.login-content_style1 {
  width: 300px;
  height: 300px;
  margin-left: 1150px;
  margin-top: 120px;
  padding: 20px;
  background-color: rgba(255, 255, 255, 0.75);
  border-radius: 10px;
  box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.8);
  .title {
	margin-bottom: 10px;
	line-height: 30px;
	font-size: 25px;
	font-style: italic;
	text-align: center;
	font-weight: bold;
	color: #19be6b;
  }
  .content {
	.login-btn {
	  text-align: center;
	  button {
		width: 100%;
		height: 40px;
		font-size: 14px;
	  }
	}
  }
  //覆盖iview样式
  .ivu-form-item {
	margin-bottom: 18px;
	.ivu-input-group-prepend {
	  width: 36px;
	  background: white;
	}
	.ivu-input {
	  height: 44px;
	  width: 220px !important;
	}
  }
}
