	/*滚动条整体样式*/
	width: 4;
}
#main::-webkit-scrollbar-thumb {
	/*滚动条里面小方块*/
	border-radius: 10px;
	// -webkit-box-shadow: inset 0 0 5px rgba(0, 0, 0, 0.2);
	background: rgba(255, 64, 64, 0.8);
}
#main::-webkit-scrollbar-track {
	/*滚动条里面轨道*/
	// -webkit-box-shadow: inset 0 0 5px rgba(0, 0, 0, 0.2);
	border-radius: 10px;
	height: 25px;
}
