result:
.ivu-tabs-bar {
	margin-bottom: 0;
}
.layout-header-bar {
	background: #fff;
	box-shadow: 0 1px 1px rgba(0, 0, 0, 0.1);
}
#viewDiv {
	padding: 0;
	margin: 0;
	width: 100%;
	height: 100%;
}

.value {
	display: inline-block;
	zoom: 1;
	_zoom: 1;
	*display: inline;
	padding-left: 8px;
}

.price {
	color: #e43;
	font-size: 14px;
	font-weight: 700;
	vertical-align: baseline;
	margin-right: 5px;
}

.key {
	font-size: 14px;
	display: inline-block;
	white-space: nowrap;
}

.label-l {
	min-width: 70px;
	color: #666;
	text-align: right;
	vertical-align: top;
	white-space: nowrap;
	padding: 5px 0;
	line-height: 24px;
}

.label-r {
	width: 74px;
	text-align: right;
	color: #666;
	vertical-align: top;
	white-space: nowrap;
	padding: 5px 0;
	line-height: 24px;
}

.text-l {
	line-height: 24px;
	vertical-align: top;
	padding: 5px 40px 5px 4px;
}

.text-r {
	line-height: 24px;
	overflow: hidden;
	display: inline-block;
}

tr {
	display: table-row;
	vertical-align: inherit;
	border-color: inherit;
}

td {
	margin: 5px;
}

.ivu-form-item {
	margin-bottom: 1px;
}

.ivu-divider-horizontal {
	display: block;
	height: 1px;
	width: 100%;
	min-width: 100%;
	margin: 15px 0;
	clear: both;
}

.list {
	height: 600px;
}
.around {
	height: 600px;
	width: 1250px;
	margin: 0 auto;
	.title {
		font-size: 22px;
		// margin-top: 80px;
		margin-bottom: 5px;
		font-weight: 600;
		color: #101d37;
	}
	ul {
		margin-left: -8px;
		.select:after {
			content: "";
			position: absolute;
			bottom: -4px;
			left: 0;
			width: 100%;
			height: 2px;
			background: #3072f6;
		}
		li {
			cursor: pointer;
			display: inline-block;
			line-height: 28px;
			padding: 0 9px;
			margin-bottom: 5px;
			color: #555;
			font-size: 14px;
		}
		.default-active {
			color: #3072f6;
			// background: #f00;
		}
		li:hover {
			color: #3072f6;
		}
	}
}

.around .list {
	//   width: 310px;
	height: 580px;
	background-color: #fff;
	box-shadow: 0 0 7px rgba(0, 0, 0, 0.45);
	.name {
		line-height: 39px;
		text-align: center;
		font-size: 14px;
		font-weight: 700;
		color: #fff;
		background: #3072f6 !important;
	}
	.ul-box {
		height: 550px;
		overflow-y: scroll;
	}
}

.page-footer {
	margin-left: 10px;
}
