result:
.typer{
  margin-top: 2%;
  box-sizing: border-box;
}
.typer .typer-content{
  font-weight: bold;
  font-size: 50px;
  display: flex;
  flex-direction: row;
  letter-spacing: 2px }
.typer-dynamic{
  position: relative;
}
.cut{
  // color: @thePink;
	font-family: "微软雅黑";
	font-size: 42px;
	line-height: 50px;
	font-weight: bold;
	background-image: -webkit-gradient(
	linear,
	0 0,
	0 bottom,
	from(#ff6145),
	to(rgb(0, 0, 255))
}
.typer-cursor{
  position: absolute;
  height: 100%;
  width: 3px;
  top: 0;
  right: -10px;
  background-color: @thePink;
  animation: flash 1.5s linear infinite;
}
