result:
.list-item {
  padding: 5px;
  cursor: pointer;
  .marker-Icon {
    margin-left: 7px;
  }
  .title {
    line-height: 30px;
    font-weight: bold;
    font-size: 14px;
    color: #19be6b;
    overflow: hidden;
    padding-left: 15px;
    text-overflow: ellipsis;
    white-space: nowrap;
  }
  .attr {
    color: #666;
  }
  .label {
    text-align: right;
  }
  &:hover {
    background: #f6f6f6;
  }
}
