.drag_box {
	position: fixed;
	float: left;
	top: 10%;
	left: 0.1%;
	z-index: 9999;
	width: 2%;
	height: 650px;
	border-radius: 0 8px 0 0px;
	background-color: rgba(0, 0, 0, 0.8);
}

#navControl {
	cursor: pointer;
	width: 100%;
	height: 50px;
	border-style: none;
	outline: none;
	color: white;
	border-radius: 0 8px 0 0px;
	display: inline-block;
	font-family: "华文中宋";
	background-color: rgba(0,0,0,0.5);
	text-align: center;
	font-size: 17px;
}

.a {
	width: 100%;
	height: 150px;
	font-weight: 900;
	text-decoration: none;
	display: inline-block;
	vertical-align: middle;
	text-align: center;
	box-shadow: 0px 2px 2px rgba(0, 0, 0, 0.9);
	backface-visibility: hidden;
}

